          </div>
        </div>
        {isPlusClicked && (
          <>
            <div className="new-task-assign-div">
              <input
                type="text"
                placeholder="Assign new todo..."
                onChange={(e) => SetInput(e.target.value)}
              />
              <div className="close-icon" onClick={() => setPlusClicked(false)}>
                <img src={close} style={{ width: "20px" }} alt="close-icon" />
              </div>
              <div className="user-icon">
                <img src={user} style={{ width: "20px" }} alt="user-icon" />
              </div>
              <div className="save-btn">
                <span onClick={handlePostRequest}>Save</span>
              </div>
            </div>
          </>
        )}
        <div className="todo-taskimage src-div">
          <TaskCard
            setPlusClicked={setPlusClicked}
            isPlusClicked={isPlusClicked}
          />
        </div>
      </div>
    </div>
  );
}

export default TodoBox;
